//finds all days present in a range
function range_finder(range){
    var sp = split(range,"-")
    var start = When(sp[0]=="Su",0, sp[0]=="Mo",1,sp[0]=="Tu",2,sp[0]=="We",3,sp[0]=="Th",4,sp[0]=="Fr",5,sp[0]=="Sa",6,7)
    var end = When(sp[1]=="Su",0, sp[1]=="Mo",1,sp[1]=="Tu",2,sp[1]=="We",3,sp[1]=="Th",4,sp[1]=="Fr",5,sp[1]=="Sa",6,7)
	var i = start
	var y = 0
	var weekend = end - start
	var listed_day = []
	
    if(weekend>0){
        var days = [["Su",0],["Mo",1],["Tu",2],["We",3],["Th",4],["Fr",5],["Sa",6]]
    	for (var x in days){
    	    	if((days[x][1] >= start) && (days[x][1] <= end) &&(start != 7) && (end != 7)){
    		    listed_day[y]=days[x][0]
    		    y+=1
    		    i+=1
    		    x+=1
    		} else if((days[x][1] <= start) && (days[x][1] >= end) &&(start != 7) && (end != 7)){
    		    listed_day[y]=days[x][0]
    		    y+=1
    		    i+=1
    		    x+=1
    		}
    	}
    }else{
        var days = [["Mo",1],["Tu",2],["We",3],["Th",4],["Fr",5],["Sa",6],["Su",7],["Mo",8],["Tu",9],["We",10],["Th",11],["Fr",12],["Sa",13],["Su",14]]
        var start =When(sp[0]=="Su",0, sp[0]=="Mo",1,sp[0]=="Tu",2,sp[0]=="We",3,sp[0]=="Th",4,sp[0]=="Fr",5,sp[0]=="Sa",6, 7)
        var end =When(sp[1]=="Su",7, sp[1]=="Mo",8,sp[1]=="Tu",9,sp[1]=="We",10,sp[1]=="Th",11,sp[1]=="Fr",12,sp[1]=="Sa",13,14)
        for (var x in days){
    	    	if((days[x][1] >= start) && (days[x][1] <= end) &&(start != 8) && (end != 15)){
    		    listed_day[y]=days[x][0]
    		    y+=1
    		    i+=1
    		    x+=1
    		} else if((days[x][1] <= start) && (days[x][1] >= end) &&(start != 8) && (end != 15)){
    		    listed_day[y]=days[x][0]
    		    y+=1
    		    i+=1
    		    x+=1
    		}
        }
    }
	return listed_day
}

//Converts values with hyphens into array and replaces them in the list
function Day_Splitter(list){
    var new_days = []
    var x = (count(list))
    var t = 0
	for (var z in list){
	    var f = 0
		if(find('-', list[z][0]) >1){
		   var range = range_finder(list[z][0])
		   var num_days=count(range)
		   for (var d in range){
		    	   if (f <= num_days){
                       if(count(list[z])==2){
                          new_days[t] =[range[f], list[z][1]]//,list[z][2]]
                          
                       }else{
                          new_days[t] =[range[f], list[z][1],list[z][2]] 
                       }
             	   x +=1
				   f +=1 
				   t +=1
			   }
			}
		}else{
		   new_days[t]= list[z]
		   x +=1
		   f +=1 
		   t +=1
		}
	}
	return new_days
}

//Function to determine if time falls during open Hours
function between(value,start,end){
    var open = Date(Year(value),Month(value),day(value),(split(start,":")[0]),(split(start,":")[1]))
    var close = Date(Year(value),Month(value),day(value),(split(end,":")[0]),(split(end,":")[1]))
	
	if(value >= open && value < close){
		return "Open"
	}else{
		return "Closed"
	}
}

//Reorder array to put sunday in place 0
function reorder(array){
	var days = [["Su",0],["Mo",1],["Tu",2],["We",3],["Th",4],["Fr",5],["Sa",6]]
	var list =[]
	var old = array
	for (var x in days){
		for(var y in old){
			if(old[y][0]==days[x][0]){
				list[x]=old[y]
			}
		}   
	}
	return list
}

//Function to determine if the location is open
function Open (times){
    
	//Day and Hour Calcs for Locations
    var schedule = reorder(Day_Splitter(DayTime(replace(times," ",""))))
    var date_now = $feature.nextelect;
	var day_schedule = schedule[weekday(date_now)];
	
	var open_hours = null;
	if(count(day_schedule)==3){
		
		var start = Date(Year(date_now),Month(date_now),Day(date_now),(split(day_schedule[1],":")[0]),(split(day_schedule[1],":")[1]))
	    var end = Date(Year(date_now),Month(date_now),Day(date_now),(split(day_schedule[2],":")[0]),(split(day_schedule[2],":")[1]))
	    var start_format = "h:mmA";
	    if (Minute(start) == 0) {
		    start_format = "hA"
		}
		var end_format = "h:mmA";
		if (Minute(end) == 0) {
		    end_format = "hA"
		}
	    open_hours = Text(start, start_format) + " - " + Text(end, end_format)
	}
	return open_hours;
}

