	    var end = Date(Year(date_now),Month(date_now),Day(date_now),(split(day_schedule[2],":")[0]),(split(day_schedule[2],":")[1]))
	    var start_format = "h:mmA";
	    if (Minute(start) == 0) {
		    start_format = "hA"
		}
		var end_format = "h:mmA";
		if (Minute(end) == 0) {
		    end_format = "hA"
		}
	    open_text = Text(start, start_format) + " - " + Text(end, end_format)
		
		if (between(date_now,day_schedule[1], day_schedule[2])=="Open"){
			//The next Value is returned if the location is open
			open_text += TextFormatting.NewLine + "Open Now";
		}
		else {
			open_text += TextFormatting.NewLine + "Closed Now"
		}
		
	}
	return open_text;
}

